    jQuery(function(){

        jQuery('body').on('click', '.pagination a',function(e){
            e.preventDefault();
            jQuery('#wpdm-downloads').load(this.href);
        });

        jQuery('.wpdm-cat-link').click(function(e){
            e.preventDefault();
            jQuery('.wpdm-cat-link').removeClass('active');
            jQuery(this).addClass('active');

            var cat_id = jQuery(this).attr('rel');
            jQuery.ajax({
                type : "post",
                dataType : "json",
                url : 'https://www.mautic.org/wp-admin/admin-ajax.php',
                data : {action: "wpdm_change_cat_parent", cat_id : cat_id},
                success: function(response) {
                    console.log(response);
                    if(response.type == "success") {
                        jQuery('#inp').html(response.parent);
                    }
                }
            });

            jQuery('#wpdm-downloads').load('https://www.mautic.org/?wpdmtask=get_downloads&pg=&category='+this.rel + '&order_by=' + jQuery('#order_by').val() +'&order=' + jQuery('#order').val());

        });

        jQuery('#wpdm-cats-compact').on('change',function(e){

            var cat_id = jQuery(this).val();
            if(cat_id == -1) cat_id = 0;
            jQuery('#initc').val(cat_id);
            jQuery.ajax({
                type : "post",
                dataType : "json",
                url : 'https://www.mautic.org/wp-admin/admin-ajax.php',
                data : {action: "wpdm_change_cat_parent", cat_id : cat_id},
                success: function(response) {
                    console.log(response);
                    if(response.type == "success") {
                        jQuery('#inp').html(response.parent);
                    }
                }
            });

            jQuery('#wpdm-downloads').load('https://www.mautic.org/?wpdmtask=get_downloads&pg=&category='+cat_id + '&order_by=' + jQuery('#order_by').val() +'&order=' + jQuery('#order').val());

        });

        jQuery('body').on('click', '.wpdm-cat-link2', function(e){

            e.preventDefault();
            jQuery('.wpdm-cat-link').removeClass('active');
            var new_rel = jQuery(this).attr('test_rel');
            if( new_rel !== 'undefined') {
                jQuery('a[rel=' + new_rel + ']').addClass('active');
            }

            var cat_id = jQuery(this).attr('rel');
            jQuery.ajax({
                type : "post",
                dataType : "json",
                url : 'https://www.mautic.org/wp-admin/admin-ajax.php',
                data : {action: "wpdm_change_cat_parent", cat_id : cat_id},
                success: function(response) {
                    console.log(response);
                    if(response.type == "success") {
                        jQuery('#inp').html(response.parent)
                    }
                }
            });


            jQuery('#wpdm-downloads').load('https://www.mautic.org/?wpdmtask=get_downloads&pg=&category='+this.rel + '&order_by=' + jQuery('#order_by').val() +'&order=' + jQuery('#order').val());

        });

        jQuery('#order_by, #order').on('change',function(){
            jQuery('#wpdm-downloads').load('https://www.mautic.org/?wpdmtask=get_downloads&pg=&category='+jQuery('#initc').val() + '&search=' + encodeURIComponent(jQuery('#src').val() ) + '&order_by=' + jQuery('#order_by').val() +'&order=' + jQuery('#order').val());
        });


        jQuery('#srcp').submit(function(e){
            e.preventDefault();
            jQuery('.wpdm-cat-link').removeClass('active');

            jQuery('#inp').html('Search Result For '+htmlEncode(jQuery('#src').val())+'');
            jQuery('#wpdm-downloads').load('https://www.mautic.org/?wpdmtask=get_downloads&pg=&search='+encodeURIComponent(jQuery('#src').val() )+'&category='+jQuery('#initc').val() + '&order_by=' + jQuery('#order_by').val() +'&order=' + jQuery('#order').val() );

        });
        jQuery('#wpdmap-home').click(function(e){
            e.preventDefault();
